import {takeEvery} from 'redux-saga/effects';

import { firestore, coventCollectionsSnapshotToMap } from '../../firebase/firebase.utils';

import ShopActionsTypes from './shop.types';

export function* fetchCollectionsAsync() {
  yield console.log('I am fired')

  const collectionRef = firestore.collection('collections');

  collectionRef
    .get()
    .then(snapshot => {
    const collectionsMap = coventCollectionsSnapshotToMap(snapshot);
    dispatch(fetchCollectionsSuccess(collectionsMap));
  })
    .catch(error => dispatch(fetchCollectionsFailure(error.message)));
}

export function* fetchCollectionsStart() {
  yield takeEvery(ShopActionsTypes.FETCH_COLLECTIONS_START, fetchCollectionsAsync)
}
